import{_ as t,o as a,c as o,ae as n}from"./chunks/framework.Dn7Y7LSn.js";const u=JSON.parse('{"title":"Notifications","description":"","frontmatter":{},"headers":[],"relativePath":"guide/notifications.md","filePath":"guide/notifications.md"}'),i={name:"guide/notifications.md"};function r(d,e,s,c,h,l){return a(),o("div",null,[...e[0]||(e[0]=[n('<h1 id="notifications" tabindex="-1">Notifications <a class="header-anchor" href="#notifications" aria-label="Permalink to &quot;Notifications&quot;">​</a></h1><p>foxd can send notifications when network events occur. Notification channels are stored in the database and managed from the web console or the REST API. Rules reference these channels by name.</p><h2 id="supported-channels" tabindex="-1">Supported Channels <a class="header-anchor" href="#supported-channels" aria-label="Permalink to &quot;Supported Channels&quot;">​</a></h2><h3 id="telegram" tabindex="-1">Telegram <a class="header-anchor" href="#telegram" aria-label="Permalink to &quot;Telegram&quot;">​</a></h3><p>Send notifications via a Telegram bot.</p><p>To set up a Telegram bot:</p><ol><li>Message <a href="https://t.me/BotFather" target="_blank" rel="noreferrer">@BotFather</a> on Telegram and create a new bot</li><li>Copy the bot token</li><li>Send a message to your bot, then use the Telegram API to find your chat ID</li></ol><h3 id="ntfy" tabindex="-1">ntfy <a class="header-anchor" href="#ntfy" aria-label="Permalink to &quot;ntfy&quot;">​</a></h3><p>Send notifications to an <a href="https://ntfy.sh" target="_blank" rel="noreferrer">ntfy</a> topic.</p><h3 id="webhook" tabindex="-1">Webhook <a class="header-anchor" href="#webhook" aria-label="Permalink to &quot;Webhook&quot;">​</a></h3><p>Send a POST request to any URL.</p><h2 id="managing-channels" tabindex="-1">Managing Channels <a class="header-anchor" href="#managing-channels" aria-label="Permalink to &quot;Managing Channels&quot;">​</a></h2><p>Use the console UI (Configuration -&gt; Notification Channels) to add or remove channels. You can also manage channels via the REST API. See the <a href="/guide/notification-channels.html">Notification Channel Management</a> guide for examples and payloads.</p><h2 id="rules" tabindex="-1">Rules <a class="header-anchor" href="#rules" aria-label="Permalink to &quot;Rules&quot;">​</a></h2><p>Notifications are triggered by rules. Rules are managed through the web console or the REST API.</p><p>Each rule has:</p><table tabindex="0"><thead><tr><th>Field</th><th>Description</th></tr></thead><tbody><tr><td><code>name</code></td><td>Human-readable name for the rule</td></tr><tr><td><code>description</code></td><td>Optional description</td></tr><tr><td><code>trigger_type</code></td><td>Event that fires the rule</td></tr><tr><td><code>mac_filter</code></td><td>Optional MAC address to limit the rule to a specific device</td></tr><tr><td><code>enabled</code></td><td>Whether the rule is active</td></tr><tr><td><code>notification_channels</code></td><td>Which notification channels to use</td></tr></tbody></table><h3 id="trigger-types" tabindex="-1">Trigger Types <a class="header-anchor" href="#trigger-types" aria-label="Permalink to &quot;Trigger Types&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Type</th><th>Fires when</th></tr></thead><tbody><tr><td><code>new_device</code></td><td>A device is seen for the first time</td></tr><tr><td><code>device_connected</code></td><td>A device comes online</td></tr><tr><td><code>device_disconnected</code></td><td>A device goes offline</td></tr><tr><td><code>device_status_change</code></td><td>A device changes status (online/offline)</td></tr></tbody></table>',19)])])}const p=t(i,[["render",r]]);export{u as __pageData,p as default};
